import React, { useState } from 'react';
import { Stage, Layer, Line, Circle } from 'react-konva';

const Page = ({ numRows, numCols }) => {
    const width = 800;
    const height = 600;
    const marginTop = 100;
    const marginBottom = 60;
    const marginLeft = 150;
    const marginRight = 40;

    let scale = (width / numRows) > (height / numCols) ? Math.floor(height / numCols) : Math.floor(width / numRows); // �chelle pour les graduations
    scale = 50;

    const [points, setPoints] = useState([]); // �tat pour les points

    const renderHorizontalLines = () => {
        const lines = [];
        for (let i = 0; i <= numRows; i++) {
            lines.push(
                <Line
                    key={`hline${i}`}
                    points={[0, marginTop + i * scale, width, marginTop + i * scale]}
                    stroke="#ddd"
                />
            );
        }
        return lines;
    };

    const renderVerticalLines = () => {
        const lines = [];
        for (let i = 0; i <= numCols; i++) {
            lines.push(
                <Line
                    key={`vline${i}`}
                    points={[marginLeft + i * scale, 0, marginLeft + i * scale, height]}
                    stroke="#ddd"
                />
            );
        }
        return lines;
    };

    // Calculer l'intersection la plus proche
    const getClosestIntersection = (x, y) => {
        const closestX = Math.round((x - marginLeft) / scale) * scale;
        const closestY = Math.round((y - marginTop) / scale) * scale;

        return { x: closestX + marginLeft, y: closestY + marginTop };
    };

    const isValidPoint = (x, y) => {
        return ((x > marginLeft - 20) && (x < width - marginRight - 20) && (y > marginTop - 20) && (y < height - marginBottom - 20));
    }

    const [mousePos, setMousePos] = useState({ x: 0, y: 0 }); // �tat pour la position de la souris

    // G�rer le mouvement de la souris 
    const handleMouseMove = (event) => {
        let { x, y } = event.target.getStage().getPointerPosition();
        const { x: closestX, y: closestY } = getClosestIntersection(x, y);

        if (isValidPoint(x, y)) {
            setMousePos({ x: closestX, y: closestY });
        }
    };

    // G�rer le clic
    const handleClick = (event) => {
        let { x, y } = event.target.getStage().getPointerPosition();
        const { x: closestX, y: closestY } = getClosestIntersection(x, y);

        if (isValidPoint(x, y)) {
            // Ajouter le point
            setPoints((prevPoints) => [...prevPoints, { x: closestX, y: closestY }]);
        }
    };

    return (
        <Stage
            width={marginLeft + width + marginRight}
            height={marginTop + height + marginBottom}
            onClick={handleClick}
            onMouseMove={handleMouseMove}
        >
            <Layer>
                {renderHorizontalLines()}
                {renderVerticalLines()}

                {/* Axes */}
                <Line points={[0, 0, width, 0]} stroke="black" />
                <Line points={[0, 0, 0, height]} stroke="black" />

                {/* Rendu des points */}
                {points.map((point, index) => (
                    <Circle
                        key={index}
                        x={point.x}
                        y={point.y}
                        radius={5}
                        fill="red"
                    />
                ))}

                {/* Cercle qui suit la souris */}
                {isValidPoint(mousePos.x, mousePos.y) && (
                    <Circle
                        x={mousePos.x}
                        y={mousePos.y}
                        radius={10}
                        fill="red"
                        opacity={0.5} // Opacit� de 50%
                    />
                )}

            </Layer>
        </Stage>
    );
};

// Valeurs par d�faut pour les props
Page.defaultProps = {
    numRows: 22,
    numCols: 10,
};

export default Page;
