import React from 'react';
import Page from './Page';

const GameContainer = ({ numRows, numCols, plateau, setPlateau }) => {
    return (
        <div className="game-container">
            <div className="score-container">
                <div id="scorePlayer1" className="score"></div>
                <div id="timer" className="score"></div>
                <div id="scorePlayer2" className="score"></div>
            </div>

            <div id="menu">
                <button id="quitter-button">Quitter</button>
                <button id="finish-button">Terminer</button>
                <button id="pauseResumeButton">Pause</button>
            </div>

            <div className="canvas-wrapper" >
                <div className="canvas-container" >
                    <Page numRows={numRows - 1} numCols={numCols - 1} plateau={plateau} setPlateau={setPlateau} />
                </div>
            </div>

            <div className="zoom-slider">
                <input type="range" className="zoom-slider" id="zoomSlider" />
            </div>

            <div id="currentTurn" className="current-turn"></div>
        </div>
    );
};

export default GameContainer;
