import React, { useState } from 'react';
import axios from 'axios';
import Cahier from './Cahier';

const App = () => {
    const [plateau, setPlateau] = useState(Array.from({ length: 10 }, () => Array(10).fill(0)));

    // Fonction d'affichage de la matrice
    const renderMatrix = (matrix) => {
        return (
            <table>
                <tbody>
                    {matrix.map((row, rowIndex) => (
                        <tr key={rowIndex}>
                            {row.map((value, colIndex) => (
                                <td key={`${rowIndex}-${colIndex}`} style={{ border: '1px solid black', padding: '5px' }}>
                                    {value}
                                </td>
                            ))}
                        </tr>
                    ))}
                </tbody>
            </table>
        );
    };

    const increment = () => {/*
        console.log("aaa");
        axios.put('http://localhost:5279/api/Increment')
            .then(response => setCount(response.data))
            .catch(error => console.error('Error:', error));

        console.log("bbb");*/

        // Appel de la fonction avec les donn�es � envoyer
        sendDataToServer("streing", { x: 5, y: 5 });
    };

    const sendDataToServer = async (plateau, dernierPoint) => {
        try {
            const response = await axios.put("http://localhost:7001/api/Game", {
                plateau: "string",
                dernierPoint: dernierPoint
            }, {
                headers: {
                    'Content-Type': 'application/json'
                }
            });
            console.log("aaaaaaaaaaaaaaa");
            console.log(response.data);
        } catch (error) {
            console.error('Une erreur s\'est produite lors de l\'envoi des donn�es au serveur :', error);
        }
    };

    return (
        <div style={{ display: 'flex', justifyContent: 'center', alignItems: 'center' }}>
            <div style={{ marginRight: '20px' }}>
                <Cahier plateau={plateau} setPlateau={setPlateau} />
            </div>
            <div>
                <h2>Matrice Plateau:</h2>
                {renderMatrix(plateau)}
            </div>
            <button onClick={increment}>Increment</button>
        </div>
    );
};

export default App;
