import React from 'react';
import { Stage, Layer, Line, Text } from 'react-konva';

const Page = () => {
    const width = 800;
    const height = 600;
    const scale = 100; // �chelle pour les graduations

    const renderHorizontalLines = () => {
        const lines = [];
        for (let i = 1; i <= width / scale; i++) {
            lines.push(
                <Line
                    key={`hline${i}`}
                    points={[-width, i * scale, width, i * scale]}
                    stroke="#ddd"
                />
            );
        }
        return lines;
    };

    const renderVerticalLines = () => {
        const lines = [];
        for (let i = 1; i <= height / scale; i++) {
            lines.push(
                <Line
                    key={`vline${i}`}
                    points={[i * scale, -height, i * scale, height]}
                    stroke="#ddd"
                />
            );
        }
        return lines;
    };

    return (
        <Stage width={width} height={height}>
            <Layer>
                {renderHorizontalLines()}
                {renderVerticalLines()}

                <Line points={[0, 0, width, 0]} stroke="black" />
                <Line points={[0, 0, 0, height]} stroke="black" />

                <Text x={width / 2} y={10} text="X" />
                <Text x={10} y={height / 2} text="Y" />
            </Layer>
        </Stage>
    );
};

export default Page;
