import React , { useEffect} from 'react';
import '../css/accueil.css';

function Home(){
    var [begin,setBegin] = useState(false);

    useEffect(() => {
        const container = document.getElementById('frontView');

        function getRandomColor() {
            const hexadecimal = '0123456789ABCDEF';
            let color = '#';
            for (let i = 0; i < 6; i++) {
                color += hexadecimal[Math.floor(Math.random() * 16)];
            }
            return color;
        }

        function createDiamond() {
            const diamond = document.createElement('div');
            diamond.className = 'diamond';
            diamond.style.backgroundColor = getRandomColor();
            diamond.style.left = `${Math.random() * window.innerWidth}px`;
            diamond.style.top = `${Math.random() * window.innerHeight}px`;
            container.appendChild(diamond);

            setTimeout(() => {
                container.removeChild(diamond);
            }, 1000);
        }

        //change view
        if(begin){

        }else{

        }

        setInterval(createDiamond, 200); 
    },[]);
    return (
        <div className="accueil">
            <div className="frontView" id="frontView">
                <p className="text" id="text" onClick={setBegin(!begin)}>
                    <span className="letter">P</span><span className="letter">O</span><span className="letter">I</span><span className="letter">N</span><span className="letter">T</span> 
                    <span className="letter"> - </span> 
                    <span className="letter">G</span><span className="letter">A</span><span className="letter">M</span><span className="letter">E</span>
                </p>
            </div>
            <div className="gameSelect">
                <div className="button Local">
                    <p>Local</p>
                </div>
                <div className="button bot">
                    <p>VS Computer</p>
                </div>
            </div>
        </div>
    );
}
export default Home;
