import React from 'react';
import { Stage, Layer, Line, Text } from 'react-konva';

const Page = ({ numRows, numCols }) => {
    const width = 800;
    const height = 600;

    const scale = width > height ? Math.floor(height / numCols) : Math.floor(width / numRows); // �chelle pour les graduations

    // Ajuste le nombre de lignes horizontales en fonction de numRows
    const renderHorizontalLines = () => {
        const lines = [];
        for (let i = 1; i <= numRows; i++) {
            lines.push(
                <Line
                    key={`hline${i}`}
                    points={[0, i * scale, width, i * scale]}
                    stroke="#ddd"
                />
            );
        }
        return lines;
    };

    // Ajuste le nombre de lignes verticales en fonction de numCols
    const renderVerticalLines = () => {
        const lines = [];
        for (let i = 1; i <= numCols; i++) {
            lines.push(
                <Line
                    key={`vline${i}`}
                    points={[i * scale, 0, i * scale, height]}
                    stroke="#ddd"
                />
            );
        }
        return lines;
    };

    return (
        <Stage width={width} height={height}>
            <Layer>
                {renderHorizontalLines()}
                {renderVerticalLines()}

                {/* Axes */}
                <Line points={[0, 0, width, 0]} stroke="black" />
                <Line points={[0, 0, 0, height]} stroke="black" />

                {/* �tiquettes pour les axes */}
                <Text x={width - 20} y={-10} text="X" />
                <Text x={-20} y={height - 20} text="Y" />
            </Layer>
        </Stage>
    );
};

// Valeurs par d�faut pour les props si elles ne sont pas fournies
Page.defaultProps = {
    numRows: 10,
    numCols: 10,
};

export default Page;
