import React, { useState } from 'react';
import { Stage, Layer, Line, Circle } from 'react-konva';

const Page = ({ numRows, numCols }) => {
    const width = 800;
    const height = 600;
    const marginTop = 50;
    const marginBottom = 50;
    const marginLeft = 50;
    const marginRight = 50;

    const scale = (width / numRows) > (height / numCols) ? Math.floor(height / numCols) : Math.floor(width / numRows); // �chelle pour les graduations

    const [points, setPoints] = useState([]); // �tat pour les points

    const renderHorizontalLines = () => {
        const lines = [];
        for (let i = 0; i <= numRows; i++) {
            lines.push(
                <Line
                    key={`hline${i}`}
                    points={[0, marginTop + i * scale, width, i * scale]}
                    stroke="#ddd"
                />
            );
        }
        return lines;
    };

    const renderVerticalLines = () => {
        const lines = [];
        for (let i = 0; i <= numCols; i++) {
            lines.push(
                <Line
                    key={`vline${i}`}
                    points={[i * scale, 0, i * scale, height]}
                    stroke="#ddd"
                />
            );
        }
        return lines;
    };

    // Calculer l'intersection la plus proche
    const getClosestIntersection = (x, y) => {
        const closestX = Math.round(x / scale) * scale;
        const closestY = Math.round(y / scale) * scale;
        return { x: closestX, y: closestY };
    };

    // G�rer le clic
    const handleClick = (event) => {
        const { x, y } = event.target.getStage().getPointerPosition();
        const { x: closestX, y: closestY } = getClosestIntersection(x, y);

        // Ajouter le point
        setPoints((prevPoints) => [...prevPoints, { x: closestX, y: closestY }]);
    };

    return (
        <Stage width={marginLeft + width + marginRight} height={marginTop + height + marginBottom} onClick={handleClick} >
            <Layer>
                {renderHorizontalLines()}
                {renderVerticalLines()}

                {/* Axes */}
                <Line points={[0, 0, width, 0]} stroke="black" />
                <Line points={[0, 0, 0, height]} stroke="black" />

                {/* Rendu des points */}
                {points.map((point, index) => (
                    <Circle
                        key={index}
                        x={point.x}
                        y={point.y}
                        radius={5}
                        fill="red"
                    />
                ))}
            </Layer>
        </Stage>
    );
};

// Valeurs par d�faut pour les props
Page.defaultProps = {
    numRows: 6,
    numCols: 6,
};

export default Page;
