import React, { useState } from 'react';
import { Stage, Layer, Line, Text, Circle } from 'react-konva';

const Cahier = () => {
    const [points, setPoints] = useState([]);

    const width = 500;
    const height = 500;
    const xInterval = 50;
    const yInterval = 50;

    const createGraduations = (start, end, interval, vertical = false) => {
        const graduations = [];
        for (let i = start; i <= end; i += interval) {
            if (vertical) {
                graduations.push(<Line key={`graduation-y-${i}`} points={[i, 0, i, height]} stroke="#ccc" strokeWidth={1} />);
            } else {
                graduations.push(<Line key={`graduation-x-${i}`} points={[0, i, width, i]} stroke="#ccc" strokeWidth={1} />);
            }
        }
        return graduations;
    };

    const handleClick = (e) => {
        const stage = e.target.getStage();
        const { x, y } = stage.getPointerPosition();
        const newX = Math.round(x / xInterval) * xInterval;
        const newY = Math.round(y / yInterval) * yInterval;
        setPoints([...points, { x: newX, y: newY }]);
    };

    return (
        <Stage width={width} height={height} onClick={handleClick}>
            <Layer>
                <Line points={[0, height / 2, width, height / 2]} stroke="black" strokeWidth={2} />
                <Line points={[width / 2, 0, width / 2, height]} stroke="black" strokeWidth={2} />
                {createGraduations(0, width, xInterval)}
                {createGraduations(0, height, yInterval, true)}
                <Text text="0" x={width / 2 - 5} y={height / 2 + 5} />
                <Text text="x" x={width - 10} y={height / 2 + 5} />
                <Text text="y" x={width / 2 + 5} y={10} />
                {points.map((point, index) => (
                    <Circle key={index} x={point.x} y={point.y} radius={3} fill="red" />
                ))}
            </Layer>
        </Stage>
    );
};

export default Cahier;
